mapper.select:
select 

from t_groupbuy_detail
where item_id = #{itemId,jdbcType=INTEGER}
  and phase_num = #{phaseNum,jdbcType=INTEGER}
  and group_que = #{groupQue,jdbcType=INTEGER}

mapper.delete:
delete from t_groupbuy_detail
where item_id = #{itemId,jdbcType=INTEGER}
  and phase_num = #{phaseNum,jdbcType=INTEGER}
  and group_que = #{groupQue,jdbcType=INTEGER}

mapper.insert:
insert into t_groupbuy_detail (item_id, phase_num, group_que, 
  order_id, item_guige, item_yanse, 
  open_id, del_flg, c_date, 
  c_user, u_date, u_user
  )
values (#{itemId,jdbcType=INTEGER}, #{phaseNum,jdbcType=INTEGER}, #{groupQue,jdbcType=INTEGER}, 
  #{orderId,jdbcType=VARCHAR}, #{itemGuige,jdbcType=INTEGER}, #{itemYanse,jdbcType=INTEGER}, 
  #{openId,jdbcType=VARCHAR}, #{delFlg,jdbcType=CHAR}, #{cDate,jdbcType=TIMESTAMP}, 
  #{cUser,jdbcType=VARCHAR}, #{uDate,jdbcType=TIMESTAMP}, #{uUser,jdbcType=VARCHAR}
  )

mapper.update:
update t_groupbuy_detail
set order_id = #{orderId,jdbcType=VARCHAR},
  item_guige = #{itemGuige,jdbcType=INTEGER},
  item_yanse = #{itemYanse,jdbcType=INTEGER},
  open_id = #{openId,jdbcType=VARCHAR},
  del_flg = #{delFlg,jdbcType=CHAR},
  c_date = #{cDate,jdbcType=TIMESTAMP},
  c_user = #{cUser,jdbcType=VARCHAR},
  u_date = #{uDate,jdbcType=TIMESTAMP},
  u_user = #{uUser,jdbcType=VARCHAR}
where item_id = #{itemId,jdbcType=INTEGER}
  and phase_num = #{phaseNum,jdbcType=INTEGER}
  and group_que = #{groupQue,jdbcType=INTEGER}
