mapper.select:
select 

from t_item_detail
where item_id = #{itemId,jdbcType=INTEGER}
  and item_guige = #{itemGuige,jdbcType=INTEGER}
  and item_yanse = #{itemYanse,jdbcType=INTEGER}

mapper.delete:
delete from t_item_detail
where item_id = #{itemId,jdbcType=INTEGER}
  and item_guige = #{itemGuige,jdbcType=INTEGER}
  and item_yanse = #{itemYanse,jdbcType=INTEGER}
