mapper.select:
select 

from t_order_detail
where order_id = #{orderId,jdbcType=VARCHAR}
  and item_id = #{itemId,jdbcType=INTEGER}
  and item_guige = #{itemGuige,jdbcType=INTEGER}

mapper.delete:
delete from t_order_detail
where order_id = #{orderId,jdbcType=VARCHAR}
  and item_id = #{itemId,jdbcType=INTEGER}
  and item_guige = #{itemGuige,jdbcType=INTEGER}
