  )
values (#{wuliuId,jdbcType=INTEGER}, #{subId,jdbcType=INTEGER}, #{price,jdbcType=DECIMAL}, 
  #{pricePlus,jdbcType=DECIMAL}, #{unitId,jdbcType=VARCHAR}, #{unitName,jdbcType=VARCHAR}, 
  #{toArea,jdbcType=VARCHAR}, #{delFlg,jdbcType=CHAR}, #{cDate,jdbcType=TIMESTAMP}, 
  #{cUser,jdbcType=VARCHAR}, #{uDate,jdbcType=TIMESTAMP}, #{uUser,jdbcType=VARCHAR}
  )
